import { useState, useEffect } from "react";
import { Outlet, NavLink, useNavigate } from "react-router-dom";

export default function RootLayout() {

      const [showPopup, setShowPopup] = useState(false);
      const [alertMessage, setAlertMessage] = useState("");
      const [loggedIn, setLoggedIn] = useState(false);
      const navigate = useNavigate();

      const handleLoginClick = () => {
          setShowPopup(true);
      };

      const handlePopupClose = () => {
          setShowPopup(false);
      };

      const handlePopupClick = (event) => {
          if (event.target === event.currentTarget) {
          setShowPopup(false);
          }
      };

      const handleEscapeKey = (event) => {
          if (event.keyCode === 27) {
          setShowPopup(false);
          }
      };

      const handleLoginFormSubmit = (event) => {
          event.preventDefault();
          const email = event.target.email.value;
          const password = event.target.password.value;
          const rememberMe = event.target.rememberMe.checked;

          // Send email, password, and rememberMe to server for authentication
          fetch('http://localhost:5000/login', {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json'
          },
          body: JSON.stringify({ email, password, rememberMe })
          })
          .then(response => {
          if (response.ok) {
              // Authentication successful
              const loginPopup = document.getElementById('login-popup');
              loginPopup.style.display = "none";
              setLoggedIn(true);
              setShowPopup(false);
              navigate("dashboard");
          } else {
              // Authentication failed
              const alertBox = '<div class="alert alert-danger" role="alert">' +
                  'Login failed. Please try again.' +
                  '</div>'
              setAlertMessage(alertBox);
          }
          })
          .catch(error => {
          console.error('Error:', error);
          });
      };

      const handleLogoutClick = () => {
          // Call the server to remove session cookies
          fetch('http://localhost:5000/logout')
          .then((response) => {
              if (response.ok) {
              setLoggedIn(false);
              navigate("/"); // redirect to home page after logout
              } else {
              console.error("Logout failed");
              }
          })
          .catch((error) => {
              console.error("Error:", error);
          });
      };

          // useEffect(() => {
          //   fetch('/status')
          //     .then((response) => response.json())
          //     .then((data) => {
          //      console.log(data.status)
          //      setLoggedIn(boolean(data.status))
          //     })
          //     .catch((error) => console.error(error));
          // }, [window.location.pathname]);

      useEffect(() => {
          const loginPopup = document.getElementById("login-popup");
          if (showPopup) {
          loginPopup.style.display = "block";
          }
          else {
          loginPopup.style.display = "none";
          }
      }, [showPopup]);

      // useEffect(() => {
      //   const navbar_logged_in = document.getElementById("navbar-logged-in");
      //   const navbar_logged_out = document.getElementById("navbar-logged-out");
      //   if (loggedIn) {
      //     navbar_logged_in.style.display = "block";
      //     navbar_logged_out.style.display = "none";
      //   }
      //   else {
      //     navbar_logged_in.style.display = "none";
      //     navbar_logged_out.style.display = "block";
      //   }
      // }, [loggedIn]);


      useEffect(() => {
          document.addEventListener("keydown", handleEscapeKey);
          return () => {
          document.removeEventListener("keydown", handleEscapeKey);
          };
      }, []);

    return (
    <div className="root-layout">
      <header>
        <nav>
          <NavLink to="/">Home</NavLink>
          <NavLink to="players">Players</NavLink>
          <NavLink to="courts">Courts</NavLink>
          {loggedIn ? (
            <>
              <NavLink to="/dashboard">Dashboard</NavLink>
              <button onClick={handleLogoutClick} id="logout-button">Logout</button>
            </>
          ) : (
            <>
              <NavLink to="/signup">Signup</NavLink>
              <button onClick={handleLoginClick} id="login-button">Login</button>
            </>
      )}
          <NavLink to="/dashboard">Dashboard</NavLink>
          <button onClick={handleLogoutClick} id="logout-button">Logout</button>

          <NavLink to="/signup">Signup</NavLink>
          <button onClick={handleLoginClick} id="login-button">Login</button>
        </nav>

        <div className="popup" onClick={handlePopupClick} id="login-popup">
          <div className="popup-content">
            <div id="alert-container"></div>
            <button type="button" className="close" onClick={handlePopupClose}>&times;</button>
            <p>If you are not already a member you can click <a href="/signup">here</a></p>
            <form onSubmit={handleLoginFormSubmit}>
              <div className="form-group">
                <label htmlFor="email">Email:</label>
                <input type="text" className="form-control" id="email" name="email" required />
              </div>
              <div className="form-group">
                <label htmlFor="password">Password:</label>
                <input type="password" className="form-control" id="password" name="password" required />
              </div>
              <div className="form-check">
                <input type="checkbox" className="form-check-input" id="remember-me" name="rememberMe" />
                <label className="form-check-label" htmlFor="remember-me">Remember me</label>
              </div>
              <button type="submit" className="btn btn-primary">Login</button>
            </form>
            Lost your email or password?
            Click <a href="/reset-password">here</a> to get them resent to your email address.
          </div>
        </div>
      </header>
      <main>
        <Outlet />
      </main>
    </div>
  )
}
