import { useState } from "react";

import {createBrowserRouter, 
  createRoutesFromElements,
  Route, 
  RouterProvider
} from 'react-router-dom'


// pages
import Home from './pages/Home'
import Players from './pages/Players'
import Signup from './pages/Signup'
import PlayerProfile, {PlayerProfileLoader} from './pages/PlayerProfile'
import Courts, {CourtsLoader} from './pages/Courts'
import CourtProfile, {CourtProfileLoader} from './pages/CourtProfile'
import Scheduler, {CourtsPlayersLoader} from './pages/Scheduler'
import Dashboard from './pages/Dashboard'
// layouts
import RootLayout from './layouts/RootLayout'

function App() {
  const [loggedIn, setLoggedIn] = useState(false);


const router = createBrowserRouter(
  createRoutesFromElements(
    <Route path="/" element={<RootLayout loggedIn={loggedIn} setLoggedIn={setLoggedIn}/>}>
      <Route index element={<Home />} />
      <Route 
        path="signup"
        element={<Signup />} 
        />
      <Route 
        path="players" 
        element={<Players />}
        loggedIn={loggedIn} 
        />
      <Route 
        path="courts" 
        element={<Courts />} 
        loader={CourtsLoader}
        loggedIn={loggedIn}
        />
      <Route 
        path="courts/:id" 
        element={<CourtProfile />}
        loader={CourtProfileLoader}
        />
      <Route 
        path="players/:id" 
        element={<PlayerProfile />}
        loader={PlayerProfileLoader}
        />
      <Route 
        path="scheduler" 
        element={<Scheduler />}
        loader={CourtsPlayersLoader}
        loggedIn={loggedIn}
        />
      <Route 
        path="dashboard" 
        element={<Dashboard />}
        loggedIn={loggedIn}
        />
    </Route>

  )
)


  return (
    <RouterProvider router={router} />
  );
}

export default App
