import { useLoaderData, Link } from 'react-router-dom'


export default function Scheduler() {
  const data = useLoaderData()

  const [formData, setFormData] = useState({
    hitting_partner: "",
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
    console.log(formData)
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(formData)
    fetch("/schedule-session", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Success:", data);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-9">
          <div className="card mt-4">
            <div className="card-header bg-secondary text-white text-center">
              <h4 className="mb-0">Schedule a Hitting Session</h4>
            </div>
            <div className="card-body">
              <form id="my-form" className="form" action="/submit-schedule" method="post">
                <div className="form-group">
                  <label htmlFor="hitting-partner">Hitting Partner:</label>
                  <select className="form-control" id="hitting-partner" name="hitting-partner" required>
                    {data[0].map((player) => (      
                      <option 
                        key={player.id} 
                        value={player.id.toString()}
                        onChange={handleInputChange}
                        required
                        >{player.fname} {player.lname} ({player.skill_lvl})
                      </option>
                    ))}
                  </select>
                </div>
                <div className="form-group">
                  <label htmlFor="datetime">Date:</label>
                  <input 
                    type="datetime-local" 
                    id="datetime" 
                    name="datetime" 
                    onChange={handleInputChange}
                    required/>
                </div>
                <div className="form-group">
                  <label htmlFor="court">Court:</label>
                  <select className="form-control" id="court" name="court" required>
                    {data[1].map((court) => (  
                      <option 
                        key={court.id} 
                        value={court.id.toString()}
                        onChange={handleInputChange}
                        required
                        >{court.name} ({court.borough})
                      </option>
                    ))}
                  </select>
                </div>
                <div className="form-group">
                  <label>Match Type:</label>
                  <div className="btn-group btn-group-toggle" data-toggle="buttons">
                    <label className="btn btn-outline-secondary active">
                      <input 
                        type="radio" 
                        name="match_type" 
                        value="match" 

                        /> Match
                    </label>
                    <label className="btn btn-outline-secondary">
                      <input type="radio" name="match_type" value="practice" autoComplete="off"/> Practice
                    </label>
                  </div>
                </div>
                <div className="row">
                  <button 
                    type="submit" 
                    className="btn btn-primary mx-auto col-6"
                    // onSubmit={handleInputChange}
                    >Submit
                  </button>
                </div>
                {/* <!-- Hidden input field to store the session ID -->
                <input type="hidden" name="session_id" value="{{ session['id'] }}">
                <button type="submit" className="btn btn-primary col-md-3">Submit</button> */}
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
  }

// data loader
export const CourtsPlayersLoader = async () => {

    const res = await fetch('http://localhost:5000/courts-players')

    if (!res.ok) {
      throw Error('Could not fetch all courts and players')
    }

    return res.json()
  }
